import ShareModal from "../Components/ShareModal.vue";
import { apiUrl } from '../api.js';

export default {
    data() {
        return {
            qariImg: '/playlistImages/',
            letters: [],
            playlist: [],
            langCode: this.$route.params.lg,
            filter_letter: '',
            URL: apiUrl,
            search_query: false
        }
    },
    components: {
        AppButton,
        AppInput,
        AppImage,
        ShareModal,
    },
    methods: {
        async fetchLetters() {
            // Your asynchronous code here
            try {
                const e = localStorage.getItem('lang')
                const response = await axios.get(`${apiUrl}/api/get-playlist-letters-api/${e}`);
                // Do something with the data
                if (response.status == 200) {
                    this.letters = response.data.letters;
                    // console.log('res:', response.data.letters);
                }
            } catch (error) {
                // Handle errors
                console.error('Error fetching playlist:', error);
            }
        },
        async fetchPlaylist() {
            try {
                const e = localStorage.getItem('lang')
                const response = await axios.get(`${apiUrl}/api/get-playlist-api/${e}`);
                if (response.status == 200) {
                    this.playlist = response.data.playlist;
                    this.filter_letter = false;
                    this.search_query = false;
                    // console.log('res:', response.data.letters);
                }
            } catch (error) {
                // Handle errors
                console.error('Error fetching playlist:', error);
            }
        },
        async filterLetter(letter) {
            try {
                const e = localStorage.getItem('lang')
                const response = await axios.get(`${apiUrl}/api/get-search-letter-playlist-api/${letter}/${e}`);
                if (response.status == 200) {
                    this.playlist = response.data.playlist;
                    this.filter_letter = letter;
                    this.search_query = false;
                    // console.log('res:', response.data.letters);
                } else {
                    this.filter_letter = false;
                }
            } catch (error) {
                // Handle errors
                this.filter_letter = false;
                console.error('Error fetching playlist:', error);
            }
        },
        async playlistSearch(search) {
            try {
                const e = localStorage.getItem('lang')
                var query = search.target.value;
                const response = await axios.get(`${apiUrl}/api/get-search-playlist-api/${query}/${e}`);
                if (response.status == 200) {
                    if (query.length > 0) {
                        this.playlist = response.data.playlist;
                        this.search_query = true;
                    } else {
                        this.fetchPlaylist();
                    }
                    // console.log('Search:', response.data.playlist, query);
                }
            } catch (error) {
                this.search_query = false;
                // Handle errors
                console.error('Error fetching playlist:', error);
            }
        },
        getFirstLetter(name) {
            var letter = name.charAt(0);
            return letter;
        },
        myCopyText(id) {

            var copyText = document.getElementById("copyText" + id);
            // Select the text field
            copyText.select();
            copyText.setSelectionRange(0, 99999); // For mobile devices

            // Copy the text inside the text field
            navigator.clipboard.writeText(copyText.value);

            // Alert the copied text
            alert("Copied the text: " + copyText.value);
        },
        async views(playlist_id) {
            var ip = localStorage.getItem('ip');
            try {
                var response = await axios.get(`${apiUrl}/api/playlist-views/${playlist_id}/${ip}`);
                // console.log(response.data);
            } catch (error) {
                console.log('error in views');
            }
        }

    },
    watch: {
        '$route.params.lg': function (newLgValue) {
            this.langCode = newLgValue;
            this.fetchLetters();
            this.fetchPlaylist();
        }
    },
    mounted() {
        this.fetchLetters();
        this.fetchPlaylist();
    }

}
</script>
